library(data.table)input<-fread("household_power_consumption.txt",skip="1/2/2007", nrows=2880)y1<-input$V1y2<-input$V2a<-as.POSIXct(paste(y1,y2), format="%d/%m/%Y %H:%M:%S")Sys.setlocale(category = "LC_TIME", locale = "C")y3<-input$V3plot(a,y1, type="l", ylab="Energy sub metering", xlab=" ")lines(a,y2, col="red")lines(a,y3, col="blue")legend( x="topright", legend=c("Sub_metering_1","Sub_metering_2", "Sub_metering_3"), col=c("black","red","blue"), lwd=1, lty=c(1,1,1))